暴力实现优先队列实现 <pre><code>  struct edge {
    int v, w;
  };

  vector&lt;edge&gt; e[maxn];
  int dis[maxn], vis[maxn];

  void dijkstra(int n, int s) {
    memset(dis, 63, sizeof(dis));
    dis[s] = 0;
    for (int i = 1; i &lt;= n; i++) {
      int u = 0, mind = 0x3f3f3f3f;
      for (int j = 1; j &lt;= n; j++)
        if (!vis[j] &amp;&amp; dis[j] &lt; mind) u = j, mind = dis[j];
      vis[u] = true;
      for (auto ed : e[u]) {
        int v = ed.v, w = ed.w;
        if (dis[v] &gt; dis[u] + w) dis[v] = dis[u] + w;
      }
    }
  }
</code></pre> <pre><code>  struct edge {
    int v, w;
  };

  struct node {
    int dis, u;

    bool operator&gt;(const node&amp; a) const { return dis &gt; a.dis; }
  };

  vector&lt;edge&gt; e[maxn];
  int dis[maxn], vis[maxn];
  priority_queue&lt;node, vector&lt;node&gt;, greater&lt;node&gt; &gt; q;

  void dijkstra(int n, int s) {
    memset(dis, 63, sizeof(dis));
    dis[s] = 0;
    q.push({0, s});
    while (!q.empty()) {
      int u = q.top().u;
      q.pop();
      if (vis[u]) continue;
      vis[u] = 1;
      for (auto ed : e[u]) {
        int v = ed.v, w = ed.w;
        if (dis[v] &gt; dis[u] + w) {
          dis[v] = dis[u] + w;
          q.push({dis[v], v});
        }
      }
    }
  }
</code></pre>
